INSERT INTO `content_manager` (`categories_id`, `parent_id`, `group_ids`, `languages_id`, `content_title`, `content_heading`, `content_text`, `sort_order`, `file_flag`, `content_file`, `download_file`, `content_status`, `content_group`, `content_delete`, `gm_link`, `gm_link_target`, `gm_priority`, `gm_changefreq`, `gm_last_modified`, `gm_sitemap_entry`, `gm_url_keywords`, `protected`) VALUES(0, 0, '', 2, 'Privatsphäre und Datenschutz', 'Privatsphäre und Datenschutz', '<p>F&uuml;gen Sie hier Ihre Informationen &uuml;ber Privatsph&auml;re und Datenschutz ein. Alternativ k&ouml;nnen Sie das Plugin <i>janolaw AGB-Hosting</i> nutzen und Ihre Datenschutzerkl&auml;rung mithilfe eines Online-Assistenten direkt zusammen mit AGB, Widerrufsbelehrung und Impressum erstellen. In nur wenigen Minuten haben Sie&nbsp;unter fachkundiger Anleitung die <strong>wichtigsten</strong> Dokumente f&uuml;r Ihren Shop <strong>individuell</strong> zusammengestellt.</p><p>Das Plugin sorgt&nbsp;daf&uuml;r, dass die Dokumente in Ihren Shop eingebunden und bei ver&auml;nderter Rechtslage <strong>automatisch aktualisiert</strong> werden. Dabei wird die aktuelle Widerrufsbelehrung zus&auml;tzlich auch in der <strong>E-Mail-Best&auml;tigung</strong> angezeigt, die Sie an Ihre Kunden verschicken. Zusammen mit&nbsp;der <strong>Abmahnkostenhaftung</strong> durch die janolaw AG sind Sie damit optimal vor Abmahnungen gesch&uuml;tzt. <a href="http://www.janolaw.de/internetrecht/agb/agb-hosting-service/gambio/?partnerid=8604#menu">Hier</a> k&ouml;nnen Sie den Service unseres Partners janolaw buchen.</p>', 3, 1, '', '', 1, 2, 0, '', '_blank', '0.0', 'always', '2008-08-26 19:59:30', 1, 'privatsphaere-und-datenschutz', 0);
INSERT INTO `content_manager` (`categories_id`, `parent_id`, `group_ids`, `languages_id`, `content_title`, `content_heading`, `content_text`, `sort_order`, `file_flag`, `content_file`, `download_file`, `content_status`, `content_group`, `content_delete`, `gm_link`, `gm_link_target`, `gm_priority`, `gm_changefreq`, `gm_last_modified`, `gm_sitemap_entry`, `gm_url_keywords`, `protected`) VALUES(0, 0, '', 2, 'AGB', 'Allgemeine Geschäftsbedingungen', '<p>Die Allgemeinen Gesch&auml;ftsbedingungen (AGB) eines Onlineshops bilden den rechtlichen Rahmen f&uuml;r alle Gesch&auml;fte, sie sind vorformulierte Vertragsbedingungen seitens des Shopbetreibers. Fehlerhafte AGB sind aber auch immer wieder ein Stolperstein f&uuml;r Onlineh&auml;ndler, denn gerade ung&uuml;ltige oder veraltete Klauseln in den Gesch&auml;ftsbedingungen sind ein h&auml;ufiger Grund f&uuml;r Abmahnungen. Die eigenen AGB immer auf dem neuesten Stand zu halten kann zeitaufw&auml;ndig und arbeitsintensiv sein.</p><p>Wir empfehlen unseren Kunden, die AGB-Services unserer Rechtspartner in Anspruch zu nehmen, mit denen die wichtigsten Rechtstexte in Ihrem Shop automatisch aktualisiert werden und Sie keine Angst vor Abmahnungen haben m&uuml;ssen:</p><p><a style="color: #0000EE;" href="https://www.it-recht-kanzlei.de/Service/gambio-agb-schnittstelle.php" title="AGB f&uuml;r Gambio-Shops von der IT-Recht-Kanzlei" target="_blank">AGB f&uuml;r Gambio-Shops von der IT-Recht-Kanzlei</a></p><p><a style="color: #0000EE;" href="http://www.janolaw.de/partner/gambio/index.html?partnerid=8604#menu" title="AGB f&uuml;r Gambio-Shops von Janolaw" target="_blank">AGB f&uuml;r Gambio-Shops von Janolaw</a></p><p>So können Sie sich zur&uuml;cklehnen und sich auf das Wesentliche konzentrieren: Ihre Kunden.</p>', 1, 1, '', '', 1, 3, 0, '', '_blank', '0.0', 'always', '2012-01-30 14:45:57', 1, 'allgemeine-geschaeftsbedingungen', 0);
