INSERT INTO `content_manager` (`categories_id`, `parent_id`, `group_ids`, `languages_id`, `content_title`, `content_heading`, `content_text`, `sort_order`, `file_flag`, `content_file`, `download_file`, `content_status`, `content_group`, `content_delete`, `gm_link`, `gm_link_target`, `gm_priority`, `gm_changefreq`, `gm_last_modified`, `gm_sitemap_entry`, `gm_url_keywords`, `protected`) VALUES(0, 0, '', 2, 'Impressum', 'Impressum', '<p>An dieser Stelle k&ouml;nnen Sie Ihr Impressum hinterlegen. Ein vollst&auml;ndiges Impressum ist f&uuml;r jeden Onlineshop rechtlich vorgeschrieben, es dient dazu, Kunden schnell und einfach Zugang zu den relevanten Informationen des Shopbetreibers zu erlauben. Dazu z&auml;hlen Name, Anschrift oder Kontaktdaten.</p><p>Wenn Sie sich nicht ganz sicher sind, welche Elemente in ein rechtssicheres Impressum geh&ouml;ren und wie diese genau dargestellt werden m&uuml;ssen, empfehlen wir Ihnen, die Dienste unserer Rechtspartner in Anspruch zu nehmen. Damit k&ouml;nnen Sie sich schnell und unkompliziert ein professionelles Impressum f&uuml;r Ihren Shop erstellen lassen, das sogar zusammen mit allen anderen Rechtstexten im Shop durch unsere Partner immer auf dem neuesten Stand der Rechtsprechung gehalten werden kann:</p><p><a style="color: #0000EE;" href="https://www.it-recht-kanzlei.de/Service/gambio-agb-schnittstelle.php" title="Impressumsservice der IT-Recht-Kanzlei" target="_blank">Impressumsservice der IT-Recht-Kanzlei</a></p><p><a style="color: #0000EE;" href="http://www.janolaw.de/partner/gambio/index.html?partnerid=8604#menu" title="Impressumsservice von Janolaw" target="_blank">Impressumsservice von Janolaw</a></p>', 0, 1, '', '', 1, 4, 0, '', '_top',   '1.0', 'always', '2008-07-29 18:01:46', 1, 'impressum', 0);
